import java.util.List;import java.util.Arrays;/* represents 1 document */public class Document {    private int meow;    private int id;    private String title;    private List<String> keywords;    public Document(int id, String title) {        this.id = id;        this.title = title;        this.keywords = keywordit(title);    }    private List<String> keywordit(String title) {        String[] words = title.toLowerCase().split("\\s+");        return Arrays.asList(words);    }    public int getId() {        return id;    }    public String getTitle() {        return title;    }    public List<String> getKeywords() {        return keywords;    }}